import React from 'react';
import { render, cleanup } from '@testing-library/react';
import TeacherTable from '../../../../../../frontend/src/components/Dashboard/teacherTable/teacherTable';

describe('TeacherTable Component', () => {
  // Clean up the DOM after each test case.
  afterEach(cleanup);

  test('It renders without crashing', () => {
    render(<TeacherTable />);
  });

  test('Correctly displays the data based on passed props', () => {
    const teacherData = [
      { id: 1, name: 'Jane Doe', subject: 'Mathematics' },
      { id: 2, name: 'John Smith', subject: 'Science' }
    ];
    const { getByTestId } = render(<TeacherTable teachers={teacherData} />);
    
    const firstTeacherName = getByTestId('teacher-name-1').textContent;
    const secondTeacherSubject = getByTestId('teacher-subject-2').textContent;

    expect(firstTeacherName).toBe('Jane Doe');
    expect(secondTeacherSubject).toBe('Science');
  });

  test('Displays "No data" when the teacher list is empty', () => {
    const { getByTestId } = render(<TeacherTable teachers={[]} />);
    expect(getByTestId('no-data')).toHaveTextContent('No data available');
  });

  // More tests could be added for error handling, async data fetching, etc.
});


